mapper.select:
select * from `order`


        and user_id=#{user_id}


        and order_status=#{orderStatus}


        and goods_name LIKE #{goodsName}

select * from `order`

    order_status != '已完成' and order_status != '已取消'

        and user_id=#{userId}


        and order_status=#{orderStatus}


        and goods_name LIKE #{goodsName}

select * from `order`

    order_status != '已下单' and order_status != '已发货'

        and user_id=#{userId}


        and order_status=#{orderStatus}


        and goods_name LIKE #{goodsName}
