mapper.select:
select * from pet


        category=#{category}


        and owned=#{owned}

mapper.insert:
INSERT INTO pet
(name, gender, birth, category, owned
,weight
,owner_id
,sterilized)
VALUES
(#{name}, #{gender}, #{birth}, #{category}, #{owned}
,#{weight}
,#{ownerId}
,#{sterilized})

mapper.update:
UPDATE pet
SET
name = #{name},
gender = #{gender}

    ,birth = #{birth}

    ,category = #{category}

    ,owned = #{owned}


    , weight = #{weight}

    , owner_id = #{ownerId}

    , sterilized = #{sterilized}

WHERE id = #{id}
